import jsonimport mysql.connectorwith open('abc.json','r') as f:    data= json.load(f)user=data["user"]password=data["password"]print(user)print(password)mydb=mysql.connector.connect(    host="localhost",    user=user,    password=password,    database="alims")print('db connected succesfully')cursor = mydb.cursor()stmt ='select customer.name,city.city,orders.id from orders,city,customer where orders.city=city.id and orders.customer=customer.id'cursor.execute(stmt)reslt = cursor.fetchall()print(reslt)mydb.close()